insert  into `kpxm`(`id`,`khxm`,`xm`,`num`,`stdscore`,`khnr`,`pfbz`,`pfqd`,`remark`,`remark1`,`remark2`) values (1,'zzld',1,1,2,'部门主要领导重视安全管理工作，纳入议事日程，时常跟踪问效。','共2分，视完成情况得分。','自评估','',NULL,'组织领导'),(2,'zzld',1,2,2,'分管领导认真履行职责，经常检查指导，督促抓好落实。','共2分，视完成情况得分。','自评估','',NULL,'组织领导'),(3,'zzld',1,3,1,'年度安全保卫工作目标明确，方案合理，措施有效，总结客观。','共1分，视完成情况得分。','自评估','',NULL,'组织领导'),(4,'gljg',2,1,3,'组织机构健全，原则上机构人数在300人以下的，视情配备1名以上专（兼）职安全管理人员；300人以上的须有机构分管，并配备2名以上专（兼）职安全管理人员。','共3分，视完成情况得分。','自评估','',NULL,'管理机构'),(5,'gljg',2,2,2,'设立处室安全员，原则上每个处室不少于1人；设立重要岗位责任人，原则上明确2人，互为AB角。','共2分，视完成情况得分。','自评估','',NULL,'管理机构'),(6,'gljg',2,3,2,'年内要对安全管理人员进行2次以上安全保卫业务教育培训。','每进行教育培训一次得1分，累计得分不超过2分。','系统统计','培训要有记录，包括并不限于：培训动态、培训图片、培训课件。',NULL,'管理机构'),(7,'zrls',3,1,5,'总行各中心要与总行、所辖处室、所属直属机构、属地一级分行，签订安全管理责任书。','按要求与总行和所属机构或上级中心签订责任书得3分，与属地一级分行及所辖处室签订责任书得2分。','自评估','',NULL,'责任落实'),(8,'gzzd',4,1,2,'依照总行制度规定，结合部门工作实际，制定人员、车辆、保密、消防、技防、交通和重要部位管理制度，并按要求严格执行，确保落实到位。','共2分，视完成情况得分。','自评估','',NULL,'规章制度'),(9,'gzzd',4,2,2,'发电机房、计算机房、以及电梯、锅炉、发电机、压力容器等特种设备运行管理制度健全，执行严格，无安全责任事故。','共2分，视完成情况得分。','自评估','',NULL,'规章制度'),(10,'gzzd',4,3,1,'食品加工、卫生防疫等专项安全管理严格，无安全责任事故。','共1分，发生安全责任事故不得分。','自评估','',NULL,'规章制度'),(11,'yhpc',5,1,4,'定期组织自查，发现问题，及时处理。','共4分，视完成情况得分。','系统统计','每月组织一次自查，每次自查得1/3分',NULL,'隐患排查'),(12,'yhpc',5,2,1,'积极配合上级业务部门、属地分行和监管部门检查监督，按要求抓好整改。','共1分，视完成情况得分。','自评估','',NULL,'隐患排查'),(13,'yhpc',5,3,2,'定期组织专业力量对工程、线路、电梯、电器、监控等设备进行安全检查和维护保养，确保安全运行。','共2分，视完成情况得分。','自评估','',NULL,'隐患排查'),(14,'yjya',6,1,2,'防抢、防盗、防骗、防火和群体性事件、自然灾害应急处置等应对重大突发事件预案健全完善。','共2分，视完成情况得分。','自评估','',NULL,'应急预案'),(15,'yjya',6,2,3,'适时组织有关人员进行实际演练，遇有情况能够妥善应对。','共3分，视完成情况得分。','系统统计','每组织一次演练得1.5分，累计得分不超过3分。',NULL,'应急预案'),(16,'barygl',7,1,2,'保安人员采购规范，无违规行为。','共2分，视完成情况得分。','自评估','',NULL,'保安人员管理'),(17,'barygl',7,2,3,'严格按照合同规定的人员数量、专业要求、素质标准，配齐配强，做到齐装满员。','共3分，视完成情况得分。','自评估','',NULL,'保安人员管理'),(18,'barygl',7,3,2,'各种应对突发事件预案健全完善，定期组织训练演练。','共2分，视完成情况得分。','系统统计','预案体系完整，每组织演练一次得1分，累计得分不超过2分。',NULL,'保安人员管理'),(19,'jfgl',8,1,3,'依照国家规定和总行要求，安装监控、报警等安防设施。','共3分，视完成情况得分。